() => ({
  template: `
  <h4>An untrusted URL:</h4>
<p><a class="e2e-dangerous-url" [href]="dangerousUrl">Click me</a></p>
<h4>A trusted URL:</h4>
<p><a class="e2e-trusted-url" [href]="dangerousUrl | safe:'url'">Click me</a></p>
`,
  props: {
    dangerousUrl: 'javascript:alert("Hi there")'
  },
})

(
  formGroup: FormGroup,
  formErrors: IFormError[]
): void => {
  const formGroupErrors: string[] = [];
  const formControlErrors: Record<string, string[]> = {};

  formErrors.forEach(item => {
    const formControlName = item.formControlName;

    if (!formControlName) {
      return formGroupErrors.push(item.message);
    }

    const formControl = formGroup.get(formControlName);

    if (formControl) {
      if (!formControlErrors[formControlName]) {
        formControlErrors[formControlName] = [];
      }
      formControlErrors[formControlName].push(item.message);
    } else {
      formGroupErrors.push(item.message);
    }
  });

  if (formGroupErrors.length) {
    formGroup.setErrors({
      customArr: formGroupErrors
    });
  }

  for (const formControlName in formControlErrors) {
    const formControl = formGroup.get(formControlName);

    formControl.setErrors({
      customArr: formControlErrors[formControlName]
    });
  }

  markAllFormControlsAsTouched(formGroup);
}

(
  a: FormControl | string,
  b: FormControl | string
) => {
  return (group: FormGroup): { [s: string]: boolean } => {
    const controlA = a instanceof FormControl ? a : group.root.get(a);
    const controlB = b instanceof FormControl ? b : group.root.get(b);

    const controlBErrors = controlB.errors;

    if (controlA && controlB && controlA.value !== controlB.value) {
      controlB.setErrors(
        Object.assign({}, controlBErrors, {
          mismatch: true
        })
      );
    } else {
      if (controlB.hasError('mismatch')) {
        const newErrors = _omit(controlBErrors, 'mismatch');
        controlB.setErrors(_isEmpty(newErrors) ? null : newErrors);
      }
    }
    return null;
  };
}

